# Cell
import os

def show_env(path, output_file):
    """

    This function will show all env on the path.

    Args:
        path : gym register file path.
        output_file :ã€€The file to store all env name.

    Return:
        env : List all env name from the path.

    """

    os.system("cat " + path + " | grep \"id=\" >> " + output_file)

    env = []
    f = open(output_file, 'r')
    for line in f.readlines():
      index = []
      flag = 0
      for i, element in enumerate(line):
        if element != "#":
          flag = 1
        if flag == 1:
          index.append(i)
      link_string = str(line)
      env.append(link_string[index[0]:])
    f.close()

    return env