def search(word, mode = 'tags', order = 'desc', period = 'all', page = 1, per_page = 30):
	params	= {
		'type': 'search',
		'word': word,
		'mode': mode,
		'order': order,
		'period': period,
		'page': page,
		'per_page': per_page
	}
	results	= rq.post(url = host, params = params).json()
	if results['status'] == 'success':
		return getResponseId(results['response'])
	else:
		return -1

