class zrcalc:
    def add(num1, num2):
        num= num1 + num2
        print(num)
    def sub(num1, num2):
        num= num1 - num2
        print(num)
    def mul(num1, num2):
        num= num1 * num2
        print(num)
    def div(num1, num2):
        num = num1 / num2
        print(num)